document.addEventListener("click",n=>{const i=n.target.matches("[data-dropdown-button]");if(i||n.target.closest("[data-dropdown]")==null){let t;i&&(t=n.target.closest("[data-dropdown]"),t.classList.toggle("active"));document.querySelectorAll("[data-dropdown].active").forEach(n=>{n!==t&&n.classList.remove("active")})}});